import React, { useEffect, useState } from "react";
import { useCandidates } from "../context/candidates-context";
import { Row, Col, Card, Container } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./candidate.css";

function Candidate() {
  const navigate = useNavigate();
  const { chosenCandidate } = useCandidates();

  const [currentCandidate, setCurrentCandidate] = useState({});

  useEffect(() => {
    if (chosenCandidate && chosenCandidate.id) {
      setCurrentCandidate(chosenCandidate);
    } else {
      navigate("../");
    }
  }, [chosenCandidate]);

  return (
    <Container className="dashboard-container">
      <Row>
        <h2>
          {currentCandidate.first_name} {currentCandidate.last_name}
        </h2>
        <Card.Img
          variant="top"
          src={currentCandidate.avatar}
          className="candidate-small-card-img"
        />{" "}
      </Row>{" "}
      <Row > candidate number: {currentCandidate.id}</Row>
      <Row> email: {currentCandidate.email}</Row>
      <Row> gender: {currentCandidate.gender}</Row>
      <Row> ocupation: {currentCandidate.job_title}</Row>
      <Row>
        {" "}
        what the candidate actualy does: {currentCandidate.job_description}
      </Row>
    </Container>
  );
}

export default Candidate;
