const URL = "localhost:8000";

export default async function fetchData(url, chosenMethod, data) {
  
  let options;
  switch (chosenMethod) {
    case "get":
      options = {
        method: "GET",
        // headers: {
        //   "Content-Type": "application/json",
        // },
      };
      break;
    case "post":
      options = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      };
      break;

    case "put":
      options = {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      };
      break;

    case "patch":
      options = {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      };
      break;

    case "delete":
      options = {
        method: "DELETE",
      };
    case "login":
      options = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      };
      break;

    default:
      console.log("wrong method", chosenMethod);
      break;
  }
  if (options.method) {
    return await fetchData(`${url}${url}`, options);
  }
}
