import React, { useEffect, useState } from "react";
import { useAuth } from "../context/auth-context";
import { validateLogin } from "../helper/validations";
import { Button, Form, Col } from "react-bootstrap";

import InputField from "../components/form/InputField";
import { Navigate } from "react-router-dom";

export default function SignIn() {
  const { login, jwt } = useAuth();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (jwt) {
      Navigate("./dashboard");
    }
  }, [jwt]);

  const handleChange = (e) => {
    const { field, value } = e.target;
    setFormData({
      ...formData,
      [field]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const gotErrors = await validateLogin(formData);
    if (Object.keys(gotErrors).length === 0) {
      login(formData);
    } else {
      setErrors(gotErrors);
    }
    // Implement your login logic here
    const { username, password } = formData;
  };

  return (
    <div className="login-container">
      <div className="inner-container">
        <h2>Login</h2>
        <h4>For Registered Users </h4>
        <Form
          noValidate
          validated={!!errors}
          onSubmit={handleSubmit}
          className="login_form"
        >
          <Form.Group as={Col} md="10" controlId="validationCustom01">
            <InputField
              lable="User Name"
              fieldName="username"
              type="text"
              onChange={handleChange}
              errors={errors}
            />
            {/* <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div> */}
            <InputField
              lable="Password"
              fieldName="password"
              type="password"
              onChange={handleChange}
              errors={errors}
            />

            {/* <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          /> */}
          </Form.Group>
          <Button type="submit">SignIn</Button>
        </Form>
        <div>
        <h4>not a member yet? you might want to sign up first</h4>
        <h4>sign up</h4>
        <h4>not a member yet? you might want to sign up first</h4>
        </div>
      </div>
    </div>
  );
}
