import React, { useEffect, useState } from "react";
import { useCandidates } from "../context/candidates-context";
import { useAuth } from "../context/auth-context";
import { Row, Col, Button, Card, Container } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";
import CradsDashboard from "../components/CradsDashboard";
import ListDashboard from "../components/ListDashboard";

function Home() {
  const {
    getCandidates,
    candidates,
    searchWord,
    paginationData,
    chooseCandidate,
  } = useCandidates();
  const { jwt } = useAuth();
  const navigate = useNavigate();
  const [isList, setIsList] = useState(false);

  const [page, setPage] = useState(0);
  const [limit, setLimit] = useState(20);
  const [currentCandidates, setCurrentCandidates] = useState([]);

  useEffect(() => {
    if (jwt) {
      getCandidates();
    } else {
      navigate("../");
    }
  }, []);

  useEffect(() => {
    if (candidates.length>0)
    setCurrentCandidates(candidates);
    console.log("currentCandidates", candidates);
  }, [candidates]);

  const changeDisplay = () => {
    setIsList(!isList);
  };

  return (
    <Container className="dashboard-container">
      <Row>
        <h2>candidates</h2>
      </Row>
      {currentCandidates.length > 0 ? (
        isList ? (
          <ListDashboard currentCandidates={currentCandidates} />
        ) : (
          <CradsDashboard currentCandidates={currentCandidates} />
        )
      ) : (
        <></>
      )}
