import React, { useEffect, useState } from "react";
import { useAuth } from "../context/auth-context";
import { validateLogin } from "../helper/validations";
import { Button, Form, Col } from "react-bootstrap";

import InputField from "../components/form/InputField";
import { useNavigate } from "react-router-dom";

export default function SignUp() {
  const { login } = useAuth();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { field, value } = e.target;
    setFormData({
      ...formData,
      [field]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const gotErrors = validateLogin(formData);
    if (Object.keys(gotErrors).length === 0) {
      login(formData);
    } else {
      setErrors(gotErrors);
    }
    // Implement your login logic here
    const { username, password } = formData;
  };

  return (
    <div className="login-container">
      <div className="inner-container">
        <h2>Login</h2>
        <h4>For Registered Users </h4>
        <Form
          noValidate
          validated={!!errors}
          onSubmit={handleSubmit}
          className="login_form"
        >
          <Form.Group as={Col} md="10" controlId="validationCustom01">
            <InputField
              lable="User Name"
              fieldName="username"
              type="text"
              onChange={(field, value) => handleChange(field, value)}
              errors={errors}
            />
            <InputField
              lable="Password"
              fieldName="password"
              type="password"
              onChange={(field, value) => handleChange(field, value)}
              errors={errors}
            />
          </Form.Group>
          <Button type="submit">SignIn</Button>
        </Form>
      </div>
      <div className="small_text_in_sign-in">
        <span>not a member yet? you might want to </span>
        <span onClick={() => navigate("/signup")} className="underline_text">
          sign up
        </span>
        <span> first</span>
      </div>
    </div>
  );
}
