import React, { useEffect, useState } from "react";
import { useAuth } from "../context/auth-context";
import validateForm from "../helper/validations";
import { Button, Form, Col } from "react-bootstrap";

import InputField from "../components/form/InputField";
import { useNavigate } from "react-router-dom";

export default function SignUp() {
  const navigate = useNavigate();
  const arrayForValidation = ["username", "email", "password"];
  const { register, jwt } = useAuth();
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (jwt) {
      navigate("./dashboard");
    }
  }, [jwt]);

  const handleChange = (field, value) => {
    setFormData({
      ...formData,
      [field]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const gotErrors = await validateForm(arrayForValidation, formData);
    await setErrors(gotErrors);
    console.log("errors", errors);
    if (Object.keys(gotErrors).length === 0) {
      register(formData);
    }
  };
  return (
    <div className="login-container">
      <div className="inner-container">
        <h2>Sign Up</h2>
        <h4>For Unregistered Users </h4>
        <Form
          noValidate
          validated={!!errors}
          onSubmit={handleSubmit}
          className="signup_form"
        >
          <Form.Group
            as={Col}
            className="signup_form_group"
            controlId="validationCustom01"
          >
            <InputField
              lable="User Name"
              fieldName="username"
              type="text"
              onChange={(field, value) => handleChange(field, value)}
              errors={errors}
            />
            <InputField
              lable="Email"
              fieldName="email"
              type="email"
              onChange={(field, value) => handleChange(field, value)}
              errors={errors}
            />
            <InputField
              lable="Password"
              fieldName="password"
              type="password"
              onChange={(field, value) => handleChange(field, value)}
              errors={errors}
            />
          </Form.Group>
          <button type="submit" className="submit_btn">
            Sign Up
          </button>{" "}
        </Form>
      </div>
      <div className="small_text_in_sign-in">
        <span>allready registered? you can </span>
        <span onClick={() => navigate("/")} className="underline_text">
          sign in
        </span>
      </div>
    </div>
  );
}
