import React from "react";
import { Pagination } from "react-bootstrap";

export default function CustomPagination({
  currentPage,
  totalPages,
  onPageChange,
}) {
  const createBtn=()=>{
    let numBtns = [];
    for (let index = 1; index < totalPages+1; index++) {
        if (index===currentPage){
            numBtns.push(<li key={index}><button onClick={()=>onPageChange(index)} className="currPage">
                {index}</button></li>)
        } else {
            numBtns.push(<li key={index}><button onClick={()=>onPageChange(index)}className="notCurrPage">
                {index}</button></li>)
        }
    }
    return numBtns;
}
  return (
      <div aria-label="pagination" className="pagination-div">
          <ul className="pagination">
              {currPage!==1 && 
                  <li><button onClick={()=>changeCurrentPage(currPage-1)} className="notCurrPage">
                      <span aria-hidden="true">«</span>
                      </button></li>
              }
              {createBtn()}
              {currPage!==totalPages && 
                  <li><button onClick={()=>changeCurrentPage(currPage+1)} className="notCurrPage">
                      <span>»</span></button></li>
              }
          </ul>
      </div>
  );
}
