import React, { createContext, useContext, useState } from "react";
import fetchData from "../services/api";
const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [jwt, setJwt] = useState(null);

  const loginCall = (userData) => {
    fetchData("/login/auth", "login", userData)
      .then((data) => {
        console.log("login data:", data);
        setJwt(data.jwt);
        setUser(data.user);
      })
      .catch((error) => {
        console.error("Error fetching users:", error);
      });
    // Implement login logic here and set the user object if successful
    setUser(userData);
  };

  const logout = () => {
    // Implement logout logic here and remove the user object
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, jwt, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
